'use strict';

/* global require, module */

const { google } = require('googleapis');
const plus = google.plus('v1');

class AuthClient {
    constructor (options) {
        this._options = options;
    }

    auth(callback) {
        plus.people.get({
            auth: this._options.key,
            userId: '+google',
        }, (err, response) => {
            if (err) {
                console.log(`Error at authenticating: ${err}`);
                return;
            }

            callback();
        });
    }
}

module.exports = AuthClient;
